import React from "react";
import { gql } from "apollo-boost";
import { useMutation } from "@apollo/react-hooks";
import { Button } from "antd";

//  title: String!
//     rating: Float
//     description_intro: String
//     language: String
//     medium_cover_image: String
// genres: [String]

const CREATE_MOVIE = gql`
  mutation getParams($title: String!) {
    addMovie(
      title: $title
      id: $id
      rating: $rating
      description_intro: $description_intro
      language: $language
      medium_cover_image: $medium_cover_image
    ) {
      title
    }
  }
`;
const Create = () => {
  const [onCreateMovie, { loading, error, data }] = useMutation(CREATE_MOVIE);
  console.log("create > ", loading, error, data);
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <input type="text" placeholder="영화명 입력" />
      <textarea minLength="3" placeholder="영화설명 입력" rows="6" />
      <select defaultValue="0">
        <option value="0">0</option>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
      </select>
      <select defaultValue="en">
        <option value="en">en</option>
        <option value="ko">ko</option>
        <option value="co">co</option>
        <option value="jp">jp</option>
      </select>
      <Button
        onClick={() => {
          onCreateMovie({ variables: { title: "test" } });
        }}
      >
        Create
      </Button>
    </div>
  );
};

export default Create;
