import React from "react";
import { gql } from "apollo-boost";
import { useQuery } from "@apollo/react-hooks";
import Movie from "../components/Movie";

const GET_MOVIES = gql`
  {
    movies {
      id
      title
      rating
      description_intro
      language
      medium_cover_image
      genres
    }
  }
`;

function Home() {
  const { loading, error, data } = useQuery(GET_MOVIES);
  return (
    <div style={{ background: "#000" }}>
      <header
        style={{
          height: "200px",
          alignItems: "center",
          display: "flex",
          width: "100%",
          textAlign: "center",
        }}
      >
        <span
          style={{
            color: "rgb(255 0 0)",
            fontWeight: "900",
            flex: 1,
            fontSize: "44px",
            fontFamily: "system-ui",
          }}
        >
          Son's Movie Collection
        </span>
      </header>
      <body style={{ display: "flex", overflow: "hidden", flexWrap: "wrap" }}>
        {loading && <div>Loading...</div>}
        {!loading &&
          data &&
          data.movies &&
          data.movies.map((movie) => {
            return <Movie key={movie.id} {...movie} />;
          })}
        {error && <div>Error!</div>}
      </body>
    </div>
  );
}
export default Home;
