import React from "react";
import { Input, Button, Dropdown, Menu, InputNumber } from "antd";

const { TextArea } = Input;
function Create() {
  console.log("create > ");

  const ratingMenu = (
    <Menu
      style={{
        background: "#ffff",
        display: "flex",
        flexDirection: "row",
        flexWrap: "wrap",
        width: "160px",
      }}
      onClick={(e) => {
        console.log("clikc > ", e);
      }}
    >
      <Menu.Item key={1}>
        <span>1</span>
      </Menu.Item>
      <Menu.Item key={2}>
        <span>2</span>
      </Menu.Item>
      <Menu.Item key={3}>
        <span>3</span>
      </Menu.Item>
      <Menu.Item key={4}>
        <span>4</span>
      </Menu.Item>
      <Menu.Item key={5}>
        <span>5</span>
      </Menu.Item>
    </Menu>
  );
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <Input placeholder="영화명을 입력" />
      <TextArea placeholder="영화설명 입력" />
      <Dropdown overlay={ratingMenu} placement="bottomCenter" arrow>
        <span>평점</span>
      </Dropdown>
      <InputNumber max={5} min={0} defaultValue={0} />
    </div>
  );
}

export default Create;
