import React from "react";
import { gql } from "apollo-boost";
import { useMutation } from "@apollo/react-hooks";
import { Button } from "antd";

const CREATE_MOVIE = gql`
  mutation getParams(
    $id: Int!
    $title: String!
    $rating: Float
    $description_intro: String
    $language: String
    $medium_cover_image: String
    $genres: [String]
  ) {
    addMovie(
      id: $id
      title: $title
      rating: $rating
      description_intro: $description_intro
      language: $language
      medium_cover_image: $medium_cover_image
      genres: $genres
    ) {
      id
      title
      rating
      description_intro
      language
      medium_cover_image
      genres
    }
  }
`;
const Create = () => {
  const [onCreateMovie, { loading, error, data }] = useMutation(CREATE_MOVIE);

  function onCreate() {
    onCreateMovie({
      variables: {
        id: 485583,
        title: "test",
        rating: 3,
        description_intro: "testtestes",
        language: "ko",
        medium_cover_image: "92923284924929",
        genres: ["drama"],
      },
    });
  }

  console.log("Create >>> ", loading, error, data);
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <input type="text" placeholder="영화명 입력" />
      <textarea minLength="3" placeholder="영화설명 입력" rows="6" />
      <select defaultValue="0">
        <option value="0">0</option>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
      </select>
      <select defaultValue="en">
        <option value="en">en</option>
        <option value="ko">ko</option>
        <option value="co">co</option>
        <option value="jp">jp</option>
      </select>
      <Button
        onClick={() => {
          onCreate();
        }}
      >
        Create
      </Button>
    </div>
  );
};

export default Create;
