import React from "react";
import { gql } from "apollo-boost";
import { useQuery } from "@apollo/react-hooks";
import { useParams } from "react-router-dom";

const GET_MOVIE = gql`
  query getById($id: String!) {
    movie(id: $id) {
      id
      title
      rating
      description_intro
      language
      medium_cover_image
      genres
    }
  }
`;

function Detail() {
  const { id } = useParams();
  const { loading, data, error } = useQuery(GET_MOVIE, {
    variables: { id: id },
  });

  const {
    title,
    rating,
    description_intro,
    language,
    medium_cover_image,
    genres,
  } = data.movie;

  return (
    <div>
      <span>{title}</span>
      <span>{description_intro}</span>
      <span>{language}</span>
      <span>{genres}</span>
      <span>{rating}</span>
      <img alt={title} src={medium_cover_image} />
    </div>
  );
}

export default Detail;
