import React, { useRef } from "react";
import { gql } from "apollo-boost";
import { useMutation } from "@apollo/react-hooks";
import { Button } from "antd";

const CREATE_MOVIE = gql`
  mutation getParams(
    $id: Int!
    $title: String!
    $rating: Float
    $description_intro: String
    $language: String
    $medium_cover_image: String
    $genres: [String]
  ) {
    addMovie(
      id: $id
      title: $title
      rating: $rating
      description_intro: $description_intro
      language: $language
      medium_cover_image: $medium_cover_image
      genres: $genres
    ) {
      id
      title
      rating
      description_intro
      language
      medium_cover_image
      genres
    }
  }
`;
const Create = () => {
  const [onCreateMovie, { loading, error, data }] = useMutation(CREATE_MOVIE);

  const _title = useRef(null);
  const _rating = useRef(null);
  const _description_intro = useRef(null);
  const _language = useRef(null);
  const _medium_cover_image = useRef(null);
  const _genres = useRef([]);

  const ratingMap = ["0", "1", "2", "3", "4", "5"];
  const languageMap = ["en", "ko", "co", "jp"];
  const genresMap = ["drama", "action", "comic", "romance"];

  function onCreate() {
    onCreateMovie({
      variables: {
        id: 485583,
        title: _title.current,
        rating: _rating.current,
        description_intro: _description_intro.current,
        language: _language.current,
        medium_cover_image: _medium_cover_image.current,
        genres: ["drama"],
      },
    });
  }

  console.log("Create >>> ", loading, error, data);
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <input
        type="text"
        placeholder="영화명 입력"
        onChange={(e) => {
          _title.current = e.target.value;
        }}
      />
      <textarea
        minLength="3"
        placeholder="영화설명 입력"
        rows="6"
        onChange={(e) => {
          _description_intro.current = e.target.value;
        }}
      />
      <select
        defaultValue="0"
        onChange={(e) => {
          _rating.current = e.target.value;
        }}
      >
        {ratingMap.map((item) => {
          return <option value="0">0</option>;
        })}
      </select>
      <select
        defaultValue="en"
        onChange={(e) => {
          _language.current = e.target.value;
        }}
      >
        <option value="en">en</option>
        <option value="ko">ko</option>
        <option value="co">co</option>
        <option value="jp">jp</option>
      </select>
      <span>
        <label htmlFor="drama">
          <input type="checkbox" id="drama" onClick={(e) => {}} />
          drama
        </label>
      </span>
      <span>
        <label htmlFor="action">
          <input type="checkbox" id="action" />
          action
        </label>
      </span>
      <span>
        <label htmlFor="comic">
          <input type="checkbox" id="comic" />
          comic
        </label>
      </span>
      <span>
        <label htmlFor="romance">
          <input type="checkbox" id="romance" />
          romance
        </label>
      </span>
      <input
        type="text"
        placeholder="영화포스터 이미지 링크 입력"
        onChange={(e) => {
          _medium_cover_image.current = e.target.value;
        }}
      />
      <Button
        onClick={() => {
          onCreate();
        }}
      >
        Create
      </Button>
    </div>
  );
};

export default Create;
