import React from "react";
import { Input, Button, InputNumber, Layout } from "antd";
import Menu from "antd/lib/menu";
import Dropdown from "antd/lib/dropdown";
const { TextArea } = Input;
function Create() {
  const ratingMenu = (
    <Menu
      style={{
        background: "#ffff",
      }}
      onClick={(e) => {
        console.log("clikc > ", e);
      }}
    >
      <Menu.Item key={1}>
        <span>1</span>
      </Menu.Item>
      <Menu.Item key={2}>
        <span>2</span>
      </Menu.Item>
      <Menu.Item key={3}>
        <span>3</span>
      </Menu.Item>
      <Menu.Item key={4}>
        <span>4</span>
      </Menu.Item>
      <Menu.Item key={5}>
        <span>5</span>
      </Menu.Item>
    </Menu>
  );
  return (
    <Layout>
      <div style={{ display: "flex", flexDirection: "column" }}>
        <Input placeholder="영화명을 입력" />
        <TextArea placeholder="영화설명 입력" />
        <Dropdown overlay={ratingMenu}>
          <span>평점</span>
        </Dropdown>
        <InputNumber max={5} min={0} defaultValue={0} />
      </div>
    </Layout>
  );
}

export default Create;
