import React, { useRef } from "react";
import { gql } from "apollo-boost";
import { useMutation, useSubscription } from "@apollo/react-hooks";
import { v4 as uuidv4 } from "uuid";

const CREATE_MOVIE = gql`
  mutation getParams(
    $id: String!
    $title: String!
    $rating: Float
    $description_intro: String
    $language: String
    $medium_cover_image: String
    $genres: [String]
  ) {
    addMovie(
      id: $id
      title: $title
      rating: $rating
      description_intro: $description_intro
      language: $language
      medium_cover_image: $medium_cover_image
      genres: $genres
    ) {
      id
      title
      rating
      description_intro
      language
      medium_cover_image
      genres
    }
  }
`;

const NEW_MOVIE = gql`
  subscription {
    movies {
      id
      title
      rating
      description_intro
      language
      medium_cover_image
      genres
    }
  }
`;

const Create = () => {
  const [onCreateMovie, { loading, error, data }] = useMutation(CREATE_MOVIE);
  const { _loading, _error, _data } = useSubscription(NEW_MOVIE);
  console.log("Home >>> ", _loading, _error, _data);
  const _title = useRef(null);
  const _rating = useRef(null);
  const _description_intro = useRef(null);
  const _language = useRef(null);
  const _medium_cover_image = useRef(null);
  const _genres = useRef([]);

  const ratingMap = ["0", "1", "2", "3", "4", "5"];
  const languageMap = ["en", "ko", "co", "jp"];
  const genresMap = ["drama", "action", "comic", "romance"];

  function onCreate() {
    onCreateMovie({
      variables: {
        id: uuidv4(),
        title: _title.current,
        rating: Number(_rating.current),
        description_intro: _description_intro.current,
        language: _language.current,
        medium_cover_image: _medium_cover_image.current,
        genres: _genres.current,
      },
    });
  }

  console.log("Create >>> ", loading, error, data);
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <div>
        <input
          type="text"
          placeholder="영화명 입력"
          onChange={(e) => {
            _title.current = e.target.value;
          }}
        />
      </div>
      <div>
        <textarea
          minLength="3"
          placeholder="영화설명 입력"
          rows="6"
          onChange={(e) => {
            _description_intro.current = e.target.value;
          }}
        />
      </div>
      <div>
        <select
          defaultValue="0"
          onChange={(e) => {
            _rating.current = e.target.value;
          }}
        >
          {ratingMap.map((rating, i) => {
            return (
              <option key={i} value={rating}>
                {rating}
              </option>
            );
          })}
        </select>
      </div>
      <div>
        <select
          defaultValue="en"
          onChange={(e) => {
            _language.current = e.target.value;
          }}
        >
          {languageMap.map((lang, i) => {
            return (
              <option key={i} value={lang}>
                {lang}
              </option>
            );
          })}
        </select>
      </div>
      <div>
        {genresMap.map((genres, i) => {
          return (
            <span key={i}>
              <label htmlFor={genres}>
                <input
                  type="checkbox"
                  id={genres}
                  onClick={(e) => {
                    if (_genres.current.includes(e.target.id)) {
                      const index = _genres.current.indexOf(e.target.id);
                      _genres.current.splice(index, 1);
                    } else {
                      _genres.current.push(e.target.id);
                    }
                  }}
                />
                {genres}
              </label>
            </span>
          );
        })}
      </div>
      <div>
        <input
          type="text"
          placeholder="영화포스터 이미지 링크 입력"
          onChange={(e) => {
            _medium_cover_image.current = e.target.value;
          }}
        />
      </div>
      <div
        onClick={() => {
          onCreate();
        }}
      >
        <span>create</span>
      </div>
    </div>
  );
};

export default Create;
