import React, { useState } from "react";

import { Link } from "react-router-dom";

function Movie(props) {
  const {
    id,
    title,
    rating,
    description_intro,
    language,
    medium_cover_image,
    genres,
  } = props;

  const [hover, setHover] = useState(false);
  console.log("hover > ", hover);

  return (
    <Link to={`/${id}`} style={{ zIndex: hover ? 1 : 0 }}>
      <div className="MoviePoster" style={{ height: "450px", display: "flex" }}>
        <img
          alt={title}
          src={medium_cover_image}
          onMouseOver={() => setHover(true)}
          onMouseLeave={() => setHover(false)}
        />
        {hover && (
          <div
            style={{
              background: "#fff",
              height: "100px",
              position: "absolute",
              right: 0,
            }}
          >
            {title}
          </div>
        )}
      </div>
    </Link>
  );
}

export default Movie;
